mapper.select:
SELECT borrow_order.order_id as orderId ,user_info.user_name as userName ,user_info.idcard ,user_info.mobile ,borrow_order.plate_number as plateNumber ,borrow_salesman.sales_name as salesName ,borrow_salesman.sales_mobile as salesMobile ,borrow_order.bo_price as boPrice ,borrow_order.product_name as productName ,borrow_order.bo_expect as boExpect ,borrow_order.bo_is_state as boIsState ,borrow_order.bo_finish_time as boFinishTime ,borrow_order.create_time as createTime ,borrow_order.pay_channel as payChannel from borrow_order LEFT JOIN user_info ON borrow_order.user_uid = user_info.uuid LEFT JOIN borrow_salesman ON borrow_order.salesman_uid = borrow_salesman.uuid WHERE 1=1
AND user_info.user_name LIKE CONCAT('%',#{userName},'%')
AND user_info.mobile = #{mobile}
AND borrow_order.plate_number = #{plateNumber}
AND borrow_order.p_code = #{pCode}
AND borrow_order.bo_is_state = #{boIsState}
AND DATE_FORMAT(borrow_order.create_time,'%Y-%m-%d') >= #{createTimeStart}
AND DATE_FORMAT(borrow_order.create_time,'%Y-%m-%d') <= #{createTimeEnd}
AND DATE_FORMAT(borrow_order.bo_finish_time,'%Y-%m-%d') >= #{boFinishTimeStart}
AND DATE_FORMAT(borrow_order.bo_finish_time,'%Y-%m-%d') <= #{boFinishTimeEnd}
AND borrow_order.pay_channel = #{payChannel}
ORDER BY borrow_order.id DESC

SELECT borrow_order.order_id as orderId ,user_info.user_name as userName ,borrow_order.plate_number as plateNumber ,borrow_order.bo_price as boPrice ,borrow_order.product_name as productName ,borrow_order.bo_expect as boExpect ,borrow_order.bo_pay_expect as boPayExpect ,borrow_order.first_pay_time as firstPayTime ,borrow_order.bo_pay_state as boPayState ,borrow_order.bo_is_state as boIsState ,borrow_order.first_expect_time as firstExpectTime ,borrow_order.last_expect_time as lastExpectTime ,borrow_order.last_pay_time as lastPayTime ,borrow_order.pay_channel as payChannel
from borrow_order LEFT JOIN user_info ON borrow_order.user_uid = user_info.uuid WHERE borrow_order.bo_is_finish = 1
AND user_info.user_name LIKE CONCAT('%',#{userName},'%')
AND user_info.mobile = #{mobile}
AND borrow_order.plate_number = #{plateNumber}
AND borrow_order.p_code = #{pCode}
AND borrow_order.bo_pay_state = #{boPayState}
AND borrow_order.pay_channel = #{payChannel}
ORDER BY borrow_order.id DESC
