mapper.select:
SELECT
borrow_order.order_id as orderId ,borrow_repayment.repay_id as repayId ,user_info.user_name as userName ,user_info.mobile as userMobile ,borrow_order.bo_price as boPrice ,borrow_order.product_name as productName ,borrow_order.bo_expect as boExpect ,borrow_repayment.repay_expect as repayExpect ,borrow_repayment.br_time as brTime ,borrow_repayment.repay_amount + borrow_repayment.punish_amount as repayAmount ,borrow_repayment.repay_status as repayStatus ,borrow_repayment.bo_repay_status as boRepayStatus ,borrow_order.bo_is_state as boIsState ,borrow_repayment.br_repay_time as brRepayTime ,borrow_repayment.surety_status as suretyStatus ,borrow_repayment.surety_time as suretyTime from borrow_repayment INNER JOIN borrow_order ON borrow_repayment.order_id = borrow_order.order_id INNER JOIN user_info ON borrow_order.user_uid = user_info.uuid WHERE borrow_order.bo_is_finish = 1
AND user_info.user_name LIKE CONCAT('%',#{userName},'%')
AND user_info.mobile = #{mobile}
AND borrow_order.plate_number = #{plateNumber}
AND borrow_order.p_code = #{pCode}
AND borrow_repayment.bo_repay_status = #{boRepayStatus}
AND borrow_repayment.br_time >= #{brTimeStart}
AND borrow_repayment.br_time <= #{brTimeEnd}
AND DATE_FORMAT(borrow_repayment.br_repay_time,'%Y-%m-%d') >= #{brRepayTimeStart}
AND DATE_FORMAT(borrow_repayment.br_repay_time,'%Y-%m-%d') <= #{brRepayTimeEnd}
AND borrow_repayment.repay_status = #{repayStatus}
ORDER BY borrow_repayment.br_time ASC
